    description: pyDescription,
    fields: { tests: pyTests },
    challengeFiles: pyChallengeFiles
  } = pyChallenge;

} } } } `; const queryRes = await queryGraphQL(query); const challenges = queryRes.data.allChallengeNode.edges.map( ({ node }) => node.challenge ); return challenges; } export function combineChallenges({ jsChallenge, pyChallenge, challengeNumber, date }: { jsChallenge: Challenge; pyChallenge: Challenge; challengeNumber: number; date: Date; }) { const { id: jsId, title: jsTitle, description: jsDescription, fields: { tests: jsTests }, challengeFiles: jsChallengeFiles } = jsChallenge; const { title: pyTitle, description: pyDescription, fields: { tests: pyTests }, challengeFiles: pyChallengeFiles } = pyChallenge;

