// Builds a block
function getCodeBlock(label: string, content?: string) {
  return `\`\`\`${label}
${typeof content !== 'undefined' ? content : ''}
\`\`\`\n`;
}

// Builds a section
function getSeedSection(content: string, label: string) {
  return content
    ? `

## --${label}--

${content}`
    : '';
}

type StepOptions = {
  challengeId: ObjectID;
  challengeSeeds: Record<string, ChallengeSeed>;
  stepNum: number;
  challengeType: number;
  isFirstChallenge?: boolean;
};

export interface ChallengeSeed {
  contents: string;
  ext: string;
  editableRegionBoundaries: number[];
  head?: string;
  tail?: string;
}

// Build the base markdown for a step
function getStepTemplate({
  challengeId,
  challengeSeeds,
  stepNum,
  challengeType,
  isFirstChallenge = false
}: StepOptions): string {
  const seedTexts = Object.values(challengeSeeds)
    .map(({ contents, ext, editableRegionBoundaries }: ChallengeSeed) => {
      let fullContents = contents;
      if (editableRegionBoundaries.length >= 2) {
        fullContents = insertErms(contents, editableRegionBoundaries);
      }
      return getCodeBlock(ext, fullContents);
    })
    .join('\n');

const seedHeads = Object.values(challengeSeeds)
    .filter(({ head }: ChallengeSeed) => head)
    .map(({ ext, head }: ChallengeSeed) => getCodeBlock(ext, head))
    .join('\n');

const seedTails = Object.values(challengeSeeds)
    .filter(({ tail }: ChallengeSeed) => tail)
    .map(({ ext, tail }: ChallengeSeed) => getCodeBlock(ext, tail))
    .join('\n');

const stepDescription = `step ${stepNum} instructions`;
  const seedChallengeSection = getSeedSection(seedTexts, 'seed-contents');
  const seedHeadSection = getSeedSection(seedHeads, 'before-user-code');
  const seedTailSection = getSeedSection(seedTails, 'after-user-code');

= contents; if (editableRegionBoundaries.length >= 2) { fullContents = insertErms(contents, editableRegionBoundaries); } return getCodeBlock(ext, fullContents); }) .join('\n'); const seedHeads = Object.values(challengeSeeds) .filter(({ head }: ChallengeSeed) => head) .map(({ ext, head }: ChallengeSeed) => getCodeBlock(ext, head)) .join('\n'); const seedTails = Object.values(challengeSeeds) .filter(({ tail }: ChallengeSeed) => tail) .map(({ ext, tail }: ChallengeSeed) => getCodeBlock(ext, tail)) .join('\n'); const stepDescription = `step ${stepNum} instructions`; const seedChallengeSection = getSeedSection(seedTexts, 'seed-contents'); const seedHeadSection = getSeedSection(seedHeads, 'before-user-code'); const seedTailSection = getSeedSection(seedTails, 'after-user-code');

const demoString = isFirstChallenge
    ? `
# demoType can either be 'onClick' or 'onLoad'. If the project or lab doesn't have a preview, delete the property
demoType: onClick`
    : '';

return (
    `---
id: ${challengeId.toString()}
title: Step ${stepNum}
challengeType: ${challengeType}
dashedName: step-${stepNum}${demoString}
---

# --description--

${stepDescription}

# --hints--

Test 1

${getCodeBlock('js')}
# --seed--` +
    seedChallengeSection +
    seedHeadSection +
    seedTailSection
  );
}

export { getStepTemplate };

// Update given value with markers (labels)
const insertErms = (seedCode: string, erms: number[]): string => {
  if (!erms || erms.length <= 1) {
    throw Error('erms should be provided');
  }

if (erms.length <= 1) {
    throw Error('erms should contain 2 elements');
  }

const separator = '\n';
  const lines = seedCode.split(separator);
  const markerLabel = '--fcc-editable-region--';

// Generate a version of seed code with the erm markers
  const newSeedCode = erms
    .slice(0, 2)
    .reduce((acc, erm) => {
      if (Number.isInteger(erm)) {
        acc.splice(erm, 0, markerLabel);
      }

return acc;
    }, lines)
    .join(separator);

return newSeedCode;
};

export { insertErms };

