export const handleRequest = (makeRequest: () => Promise<Response>) => () => {
  makeRequest()
    .then(
      res =>
        res.json() as Promise<{
          stdout?: string;
          stderr?: string;
          message?: string;
        }>
    )
    .then(data => {
      if (data.message) {
        alert(data.message);
      } else {
        alert(JSON.stringify(data));
      }
    })
    .catch(err => console.error(err));
};

