  requestedUser,
  maybeUser,
  fetchProfileForUser,
  isSessionUser,
  showLoading
}: ShowProfileOrFourOhFourProps) {
  useEffect(() => {
    // If the user is not already in the store, fetch it
    if (isEmpty(requestedUser) && maybeUser) {
      fetchProfileForUser(maybeUser);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

if (!isBrowser()) {
    return null;
  }

