const mapStateToProps = createSelector(
  isDonationModalOpenSelector,
  donatableSectionRecentlyCompletedSelector,
  (
    show: boolean,
    donatableSectionRecentlyCompleted: DonatableSectionRecentlyCompleted
  ) => ({
    show,
    donatableSectionRecentlyCompleted
  })
);

const mapDispatchToProps = (dispatch: Dispatch<AnyAction>) =>
  bindActionCreators({ closeDonationModal }, dispatch);

type DonateModalProps = {
  activeDonors?: number;
  closeDonationModal: typeof closeDonationModal;
  location?: WindowLocation;
  donatableSectionRecentlyCompleted: DonatableSectionRecentlyCompleted;
  show: boolean;
};

function DonateModal({
  show,
  closeDonationModal,
  location,
  donatableSectionRecentlyCompleted
}: DonateModalProps): JSX.Element {
  const [canClose, setCanClose] = useState(false);
  const isA11yFeatureEnabled = useFeature('a11y-donation-modal').on;

useEffect(() => {
    if (show) {
      void playTone('donation');
      callGA({ event: 'pageview', pagePath: '/donation-modal' });
      callGA({
        event: 'donation_view',
        action: `Displayed ${
          donatableSectionRecentlyCompleted !== null ? 'Block' : 'Progress'
        } Donation Modal`
      });
    }
  }, [show, donatableSectionRecentlyCompleted]);

Dispatch<AnyAction>) => bindActionCreators({ closeDonationModal }, dispatch); type DonateModalProps = { activeDonors?: number; closeDonationModal: typeof closeDonationModal; location?: WindowLocation; donatableSectionRecentlyCompleted: DonatableSectionRecentlyCompleted; show: boolean; }; function DonateModal({ show, closeDonationModal, location, donatableSectionRecentlyCompleted }: DonateModalProps): JSX.Element { const [canClose, setCanClose] = useState(false); const isA11yFeatureEnabled = useFeature('a11y-donation-modal').on; useEffect(() => { if (show) { void playTone('donation'); callGA({ event: 'pageview', pagePath: '/donation-modal' }); callGA({ event: 'donation_view', action: `Displayed ${ donatableSectionRecentlyCompleted !== null ? 'Block' : 'Progress' } Donation Modal` }); } }, [show, donatableSectionRecentlyCompleted]);

const handleModalHide = () => {
    // If modal is open on a SuperBlock page
    if (isLocationSuperBlock(location)) {
      goToAnchor('claim-cert-block');
    }

if (isA11yFeatureEnabled && canClose) {
      closeDonationModal();
    }
  };

return (
    <Modal size='large' onClose={handleModalHide} open={show}>
      <DonationModalBody
        closeDonationModal={closeDonationModal}
        donatableSectionRecentlyCompleted={donatableSectionRecentlyCompleted}
        setCanClose={setCanClose}
      />
    </Modal>
  );
}

DonateModal.displayName = 'DonateModal';

export default connect(mapStateToProps, mapDispatchToProps)(DonateModal);

