import React from 'react';
import { Trans, useTranslation } from 'react-i18next';
import { Alert, Spacer } from '@freecodecamp/ui';

import './offline-warning.css';

const delayInMilliSeconds = 5000;
let id: ReturnType<typeof setTimeout>;

interface OfflineWarningProps {
  isOnline: boolean;
  isServerOnline: boolean;
  isSignedIn: boolean;
}

function OfflineWarning({
  isOnline,
  isServerOnline,
  isSignedIn
}: OfflineWarningProps): JSX.Element | null {
  const { t } = useTranslation();
  const [showWarning, setShowWarning] = React.useState(false);
  let message;

if (!isSignedIn || (isOnline && isServerOnline)) {
    clearTimeout(id);
    if (showWarning) setShowWarning(false);
  } else {
    message = !isOnline ? (
      t('misc.offline')
    ) : (
      <Trans i18nKey='misc.server-offline'>
        <a href={'mailto:support@freecodecamp.org'}>placeholder</a>
      </Trans>
    );
    timeout();
  }

function timeout() {
    id = setTimeout(function () {
      setShowWarning(true);
    }, delayInMilliSeconds);
  }

return showWarning ? (
    <>
      <Alert variant='info' className='offline-warning'>
        {message}
      </Alert>
      <Spacer size='m' />
    </>
  ) : null;
}

OfflineWarning.displayName = 'OfflineWarning';

export default OfflineWarning;

import React from 'react';
import { render, act, screen } from '@testing-library/react';

import OfflineWarning from './offline-warning';

jest.useFakeTimers();

describe('<OfflineWarning />', () => {
  it('renders null when isOnline, isServerOnline and isSignedIn are true', () => {
    const { container } = render(
      <OfflineWarning isOnline={true} isServerOnline={true} isSignedIn={true} />
    );
    act(() => jest.runAllTimers());
    expect(container).toBeEmptyDOMElement();
  });

it('renders the misc.offline message when isOnline is false', () => {
    render(
      <OfflineWarning
        isOnline={false}
        isServerOnline={true}
        isSignedIn={true}
      />
    );
    act(() => jest.runAllTimers());
    expect(screen.getByText('misc.offline')).toBeInTheDocument();
  });

it('renders the placeholder anchor tag if isServerOnline is false', () => {
    render(
      <OfflineWarning
        isOnline={true}
        isServerOnline={false}
        isSignedIn={true}
      />
    );
    act(() => jest.runAllTimers());
    expect(screen.getByText('placeholder').tagName).toBe('A');
    expect(screen.getByText('placeholder')).toHaveAttribute(
      'href',
      'mailto:support@freecodecamp.org'
    );
  });

it('renders null when isSignedIn is false', () => {
    const { container } = render(
      <OfflineWarning
        isOnline={true}
        isServerOnline={true}
        isSignedIn={false}
      />
    );
    act(() => jest.runAllTimers());
    expect(container).toBeEmptyDOMElement();
  });

it('renders the misc.offline message when isOnline and isServerOnline are false', () => {
    render(
      <OfflineWarning
        isOnline={false}
        isServerOnline={false}
        isSignedIn={true}
      />
    );
    act(() => jest.runAllTimers());
    expect(screen.getByText('misc.offline')).toBeInTheDocument();
  });
});

