      <label htmlFor='scrollbar-width-slider'>
        {t('settings.scrollbar-width')}:
        <span
          className='scrollbar-width-preview'
          style={{ width: `${scrollbarWidth}px` } as React.CSSProperties}
        />
      </label>
      <div className='scrollbar-width-container'>
        <div className='scrollbar-width-ticks' aria-hidden='true'>
          {ticks.map(tick => (
            <span
              className='tick'
              onClick={handleClick}
              data-value={tick}
              key={`tick${tick}`}
              {...(scrollbarWidth === tick && { 'data-current': true })}
            />
          ))}
        </div>
        <input
          type='range'
          min='5'
          max='25'
          step='5'
          id='scrollbar-width-slider'
          className='scrollbar-width'
          defaultValue={scrollbarWidth}
          onInput={handleChange}
          ref={rangeRef}
        />
        <div className='scrollbar-width-numbers' aria-hidden='true'>
          {ticks.map(tick => (
            <span
              onClick={handleClick}
              data-value={tick}
              key={`num${tick}`}
              {...(scrollbarWidth === tick && { className: 'selected' })}
            >
              {tick}
            </span>
          ))}
        </div>
      </div>
      <Spacer size='m' />
    </>
  );
}

import React, { ChangeEvent, useState } from 'react';
import store from 'store';
import { useTranslation } from 'react-i18next';

import './sound.css';
import { Spacer } from '@freecodecamp/ui';
import { playTone } from '../../utils/tone';
import ToggleButtonSetting from './toggle-button-setting';

type SoundProps = {
  sound: boolean;
  toggleSoundMode: (sound: boolean) => void;
};

export default function SoundSettings({
  sound,
  toggleSoundMode
}: SoundProps): JSX.Element {
  const { t } = useTranslation();
  const [volumeDisplay, setVolumeDisplay] = useState(
    (store.get('soundVolume') as number) ?? 50
  );
  const [mayPlay, setMayPlay] = useState(true);

function handleVolumeChange(event: ChangeEvent<HTMLInputElement>) {
    const inputValue = Number(event.target.value);

store.set('soundVolume', inputValue);

setVolumeDisplay((store.get('soundVolume') as number) ?? 50);

if (mayPlay) {
      void playTone('tests-completed');
      setMayPlay(false);
      setTimeout(() => {
        setMayPlay(true);
      }, 200);
    }
  }

return (
    <>
      <ToggleButtonSetting
        action={t('settings.labels.sound-mode')}
        explain={t('settings.sound-mode')}
        flag={sound}
        flagName='sound'
        offLabel={t('buttons.off')}
        onLabel={t('buttons.on')}
        toggleFlag={() => {
          toggleSoundMode(sound ? false : true);
        }}
      />
      <label htmlFor='volumeslider'>
        {t('settings.sound-volume')}{' '}
        <span aria-hidden='true'>{volumeDisplay}</span>
      </label>
      <input
        type='range'
        min='10'
        max='100'
        id='volumeslider'
        defaultValue={volumeDisplay}
        className='soundbar'
        onInput={handleVolumeChange}
      />
      <Spacer size='m' />
    </>
  );
}

