import React from 'react';
import { render, screen, fireEvent } from '@testing-library/react';
import store from 'store';
import StagingWarningModal from '.';
describe('StagingWarningModal', () => {
  beforeAll(() => {
    // The Modal component uses `ResizeObserver` under the hood.
    // However, this property is not available in JSDOM, so we need to manually add it to the window object.
    // Ref: https://github.com/jsdom/jsdom/issues/3368
    Object.defineProperty(window, 'ResizeObserver', {
      writable: true,
      value: jest.fn(() => ({
        observe: jest.fn(),
        unobserve: jest.fn(),
        disconnect: jest.fn()
      }))
    });
  });

afterEach(() => {
    jest.clearAllMocks();
  });

it('renders the modal successfully', () => {
    render(<StagingWarningModal />);
    expect(screen.getByRole('dialog')).toBeInTheDocument();
  });

it('closes the modal when clicking the close button', () => {
    render(<StagingWarningModal />);
    fireEvent.click(screen.getByText('Close'));

expect(screen.queryByRole('dialog')).not.toBeInTheDocument();
  });

it('displays the correct modal content', () => {
    render(<StagingWarningModal />);
    const modalContent = screen.getByRole('dialog');
    expect(modalContent).toHaveTextContent('staging-warning.heading');
    expect(modalContent).toHaveTextContent('staging-warning.p1');
    expect(modalContent).toHaveTextContent('staging-warning.p2');
    expect(modalContent).toHaveTextContent('staging-warning.p3');
  });

it('accepts Warning, stores acceptance key in local storage, and closes the modal', () => {
    render(<StagingWarningModal />);

fireEvent.click(screen.getByTestId('accepts-warning'));
    expect(store.get('acceptedStagingWarning')).toBe(true);

expect(screen.queryByRole('dialog')).not.toBeInTheDocument();
  });
});

import { createAction, handleActions } from 'redux-actions';

import { createTypes } from '../../../utils/create-types';

export const ns = 'search';

const initialState = {
  query: '',
  indexName: 'news',
  isSearchDropdownEnabled: true,
  isSearchBarFocused: false
};

const types = createTypes(
  ['toggleSearchDropdown', 'toggleSearchFocused', 'updateSearchQuery'],
  ns
);

export const toggleSearchDropdown = createAction(types.toggleSearchDropdown);
export const toggleSearchFocused = createAction(types.toggleSearchFocused);
export const updateSearchQuery = createAction(types.updateSearchQuery);

export const isSearchDropdownEnabledSelector = state =>
  state[ns].isSearchDropdownEnabled;
export const isSearchBarFocusedSelector = state => state[ns].isSearchBarFocused;
export const searchQuerySelector = state => state[ns].query;

export const reducer = handleActions(
  {
    [types.toggleSearchDropdown]: (state, { payload }) => ({
      ...state,
      isSearchDropdownEnabled:
        typeof payload === 'boolean' ? payload : !state.isSearchDropdownEnabled
    }),
    [types.toggleSearchFocused]: (state, { payload }) => {
      const focussed = state.isSearchBarFocused;
      return focussed === payload
        ? state
        : { ...state, isSearchBarFocused: payload };
    },
    [types.updateSearchQuery]: (state, { payload }) => ({
      ...state,
      query: payload
    })
  },
  initialState
);

